package com.bsit.codegeneration.dao;

import org.jdbi.v3.sqlobject.statement.SqlQuery;
import org.jdbi.v3.sqlobject.statement.SqlUpdate;
import org.jdbi.v3.sqlobject.statement.GetGeneratedKeys;
import org.jdbi.v3.sqlobject.customizer.Bind;
import org.jdbi.v3.sqlobject.customizer.BindBean;
import org.jdbi.v3.sqlobject.config.RegisterRowMapper;
import com.bsit.codegeneration.record.Prodtype;
import com.bsit.codegeneration.mapper.ProdtypeMapper;
import java.util.*;

@RegisterRowMapper(ProdtypeMapper.class)
public interface ProdtypeDao {

    @SqlQuery("SELECT * FROM PRODTYPE")
    public List<Prodtype> findAll();

    @SqlQuery("SELECT * FROM PRODTYPE WHERE id = :id")
    public Optional<Prodtype> findById(@Bind("id") Long id);

    @SqlUpdate("INSERT INTO PRODTYPE(KEY97, PRODUCT, SUPERSEDED, NAME, DESCRIP, FACILITY, USELICENSE, SUPERSEDES, CODE, ROUNDING, ECFPRCNT, DEFINTYPE, USESPERATE, INT_CON_CR, INT_CONSOL, INTDISPAY, PENAL_CODE, PENAL_DATE, RATETYPE, DFUSNCEUNT, DFUSNCENUM, PENAL_RATE, DEFPERCENT, INT_CON_DR, PEPEDUNIT, PEPEDNUM, TRINTAPP, OVPUNIT, OVPNUM, MAT_ADJUST, PTNS_REQ, COST_FUNDS, COST_CUST, COFREPTYPE, COFPERUNIT, COFPERNUM, COFPERDAY, COFPERADJ, COFDRTCD, COFCRTCD, COFCONTRA, COFUPDRATE, ISLAMIC, UNEXPECTED, RATETYPEPD, DISABLEINT, DISABLECOF, PSETSTATUS, PARAMSET, TYPEFLAG, TSTAMP, INTPERUNIT, INTPERNUM, INTPERDAY, INT_ADVDYS, INT_CMPND, COF_CMPND, PD_CMPND) VALUES (:KEY97, :PRODUCT, :SUPERSEDED, :NAME, :DESCRIP, :FACILITY, :USELICENSE, :SUPERSEDES, :CODE, :ROUNDING, :ECFPRCNT, :DEFINTYPE, :USESPERATE, :INT_CON_CR, :INT_CONSOL, :INTDISPAY, :PENAL_CODE, :PENAL_DATE, :RATETYPE, :DFUSNCEUNT, :DFUSNCENUM, :PENAL_RATE, :DEFPERCENT, :INT_CON_DR, :PEPEDUNIT, :PEPEDNUM, :TRINTAPP, :OVPUNIT, :OVPNUM, :MAT_ADJUST, :PTNS_REQ, :COST_FUNDS, :COST_CUST, :COFREPTYPE, :COFPERUNIT, :COFPERNUM, :COFPERDAY, :COFPERADJ, :COFDRTCD, :COFCRTCD, :COFCONTRA, :COFUPDRATE, :ISLAMIC, :UNEXPECTED, :RATETYPEPD, :DISABLEINT, :DISABLECOF, :PSETSTATUS, :PARAMSET, :TYPEFLAG, :TSTAMP, :INTPERUNIT, :INTPERNUM, :INTPERDAY, :INT_ADVDYS, :INT_CMPND, :COF_CMPND, :PD_CMPND)")
    @GetGeneratedKeys()
    public Long insert(@BindBean() Prodtype entity);

    @SqlQuery("UPDATE PRODTYPE SET KEY97 = :KEY97, PRODUCT = :PRODUCT, SUPERSEDED = :SUPERSEDED, NAME = :NAME, DESCRIP = :DESCRIP, FACILITY = :FACILITY, USELICENSE = :USELICENSE, SUPERSEDES = :SUPERSEDES, CODE = :CODE, ROUNDING = :ROUNDING, ECFPRCNT = :ECFPRCNT, DEFINTYPE = :DEFINTYPE, USESPERATE = :USESPERATE, INT_CON_CR = :INT_CON_CR, INT_CONSOL = :INT_CONSOL, INTDISPAY = :INTDISPAY, PENAL_CODE = :PENAL_CODE, PENAL_DATE = :PENAL_DATE, RATETYPE = :RATETYPE, DFUSNCEUNT = :DFUSNCEUNT, DFUSNCENUM = :DFUSNCENUM, PENAL_RATE = :PENAL_RATE, DEFPERCENT = :DEFPERCENT, INT_CON_DR = :INT_CON_DR, PEPEDUNIT = :PEPEDUNIT, PEPEDNUM = :PEPEDNUM, TRINTAPP = :TRINTAPP, OVPUNIT = :OVPUNIT, OVPNUM = :OVPNUM, MAT_ADJUST = :MAT_ADJUST, PTNS_REQ = :PTNS_REQ, COST_FUNDS = :COST_FUNDS, COST_CUST = :COST_CUST, COFREPTYPE = :COFREPTYPE, COFPERUNIT = :COFPERUNIT, COFPERNUM = :COFPERNUM, COFPERDAY = :COFPERDAY, COFPERADJ = :COFPERADJ, COFDRTCD = :COFDRTCD, COFCRTCD = :COFCRTCD, COFCONTRA = :COFCONTRA, COFUPDRATE = :COFUPDRATE, ISLAMIC = :ISLAMIC, UNEXPECTED = :UNEXPECTED, RATETYPEPD = :RATETYPEPD, DISABLEINT = :DISABLEINT, DISABLECOF = :DISABLECOF, PSETSTATUS = :PSETSTATUS, PARAMSET = :PARAMSET, TYPEFLAG = :TYPEFLAG, TSTAMP = :TSTAMP, INTPERUNIT = :INTPERUNIT, INTPERNUM = :INTPERNUM, INTPERDAY = :INTPERDAY, INT_ADVDYS = :INT_ADVDYS, INT_CMPND = :INT_CMPND, COF_CMPND = :COF_CMPND, PD_CMPND = :PD_CMPND WHERE id = :id")
    public int update(@BindBean() Prodtype entity);

    @SqlQuery("DELETE FROM PRODTYPE WHERE id = :id")
    public int delete(@Bind("id") Long id);
}
